import React, { Component } from "react";
import { getMovieDetail, addMovieFavorite, removeMovieFavorite } from '../../actions/index';
// import { connect } from "react-redux";
// import { removeArticle } from "../../actions/index";
// import { Link } from 'react-router-dom';
import './Favorites.css';

export class Favorites extends Component {

  render() {
    return (
      <div>
        <h2>Pel√≠culas Favoritas</h2>
        <ul>
        {this.props.movies && this.props.movies.map ((movie,i) => 
         <div key={i}>
           <NavLink to={`/movie/${movie.imdbID}`}> {movie.Title} </NavLink>
           <button onClick={() => this.props.removeMovieFavorite({title: movie.Title, id: movie.imdbID})}> - Fav </button>
        </div>
        )}
        </ul>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    movies: state.movies
  };
}
// busco y despues puedo darle fav
function mapDispatchToProps(dispatch) {
  return {
    removeMovieFavorite: movie => dispatch(removeMovieFavorite(movie)),
    };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Movie);
