import React from 'react';
import { connect } from 'react-redux';
import { getMovieDetail, addMovieFavorite, removeMovieFavorite } from '../../actions/index';
import './Movie.css';

export class Movie extends React.Component {
   componentDidMount(){
       this.props.getMovieDetail(this.props.match.params.id)
   }
    render() {
        return (
              <div key={this.props.movieDetail.imdbID} className="col-xs-4">
                <br />
                <img src={this.props.movieDetail.Poster} />
                <span>Title: {this.props.movieDetail.Title}</span>
                <br />
                <span>Type: {this.props.movieDetail.Type}</span>
                <br />
                <span>Plot: {this.props.movieDetail.Plot}</span>
                <br />
                <span> Year: {this.props.movieDetail.Year}</span>
                <br />
              </div>
        );
    }
}

// lo que me interesa del estado
function mapStateToProps(state) {
    return {
      movieDetail: state.movieDetail,
      };
  }
  // busco y despues puedo darle fav
  function mapDispatchToProps(dispatch) {
    return {
     getMovieDetail: id => dispatch(getMovieDetail(id))
    };
  }
  
  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Movie);
